import { useQuery, gql } from '@apollo/client';
import StaffSection from './../components/StaffSection';
import React from 'react';
import PropTypes from 'prop-types';
import { BarChart, Bar, Cell } from 'recharts';
import { scaleOrdinal } from 'd3-scale';
import { schemeCategory10 } from 'd3-scale-chromatic';


import {
    LineChart,
    Line,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend,
    ReferenceLine,    
  } from 'recharts';
  

  const colors = scaleOrdinal(schemeCategory10).range();

  const data_chart_foot_fall = [
    {
      name: 'Jan',
      amt: 2400,
    },
    {
      name: 'Feb',
      amt: 2210,
    },
    {
      name: 'March',
      amt: 2290,
    },
    {
      name: 'Apr',
      amt: 2000,
    },
    {
      name: 'May',  
      amt: 2181,
    },
    {
      name: 'Jun', 
      amt: 2500,
    },
    {
      name: 'Jul', 
      amt: 2100,
    },
  ];




  const data_revenue = [
    {
      name: 'Jan',
      revenue: 4000
    },
    {
      name: 'Feb',
      revenue: 3000
    },
    {
      name: 'Mar',
      revenue: 2000
    },
    {
      name: 'Apr',
      revenue: 2780
    },
    {
      name: 'May',
      revenue: 1890
    },
    {
      name: 'Jun',
      revenue: 2390
    },
    {
      name: 'Jul',
      revenue: 3490
    },
  ];

const getPath = (x, y, width, height) => `M${x},${y + height}
          C${x + width / 3},${y + height} ${x + width / 2},${y + height / 3} ${x + width / 2}, ${y}
          C${x + width / 2},${y + height / 3} ${x + (2 * width) / 3},${y + height} ${x + width}, ${y + height}
          Z`;

const TriangleBar = (props) => {
  const { fill, x, y, width, height } = props;

  return <path d={getPath(x, y, width, height)} stroke="none" fill={fill} />;
};

TriangleBar.propTypes = {
  fill: PropTypes.string,
  x: PropTypes.number,
  y: PropTypes.number,
  width: PropTypes.number,
  height: PropTypes.number,
};



const STAFF_QUERY = gql`
{
    staff {
      id
      staff_name
      efficiency_d1
      efficiency_d2
      nps_d1
      nps_d2
      efficiency
      reported_Issues
      location {
        id
        location
      }
    }
  }  
`;


const FOOTFALL_QUERY = gql`
{
    footfall {
      id
      name
      amnt
    }
  } 
`;


const VisualsGraphs = () => {
const { data } = useQuery(STAFF_QUERY);
const  data_staff = data;

const data_footfall = useQuery(FOOTFALL_QUERY);
//const  data_footfall = data;





console.log(data_footfall);
 return (   
    <div class="row">
    <div class="col-lg-6 col-12">
        <div class="card">
            <div class="card-header d-flex justify-content-between align-items-end">
                <h4>Foot fall</h4>
              
            </div>
            <div class="card-content">
                <div class="card-body pt-0 chart_container">                
                    <LineChart
                            width={500}
                            height={300}
                            data={data_chart_foot_fall}
                            margin={{
                                top: 20,
                                right: 50,
                                left: 20,
                                bottom: 5,
                            }}
                            >
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis dataKey="name" />
                            <YAxis />
                            <Tooltip />
                            <Legend />
                            <ReferenceLine x="Page C" stroke="red" label="Max PV PAGE" />
                            <ReferenceLine y={9800} label="Max" stroke="red" />
                            <Line type="monotone" dataKey="amt" stroke="#8884d8" />                   
                    </LineChart>
                                      
                </div>
            </div>
        </div>
    </div>

    <div class="col-lg-6 col-12">
        <div class="card">
            <div class="card-header d-flex justify-content-between pb-0">
                <h4 class="card-title">Revenue</h4>                                  
            </div>
            <div class="card-content">
                <div class="card-body py-0 chart_container">
                        <BarChart
                            width={500}
                            height={300}
                            data={data_revenue}
                            margin={{
                                top: 20,
                                right: 30,
                                left: 20,
                                bottom: 5,
                            }}
                            >
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis dataKey="name" />
                            <YAxis />
                            <Tooltip />
                            <Legend />
                            <Bar dataKey="revenue" fill="#8884d8" shape={<TriangleBar />} label={{ position: 'top' }}>
                                {data_revenue.map((entry, index) => (
                                <Cell key={`cell-${index}`} fill={colors[index % 20]} />
                                ))}
                            </Bar>
                        </BarChart>
                </div>                             
            </div>
        </div>
    </div>














    <div class="col-lg-12 col-md-6 col-12">
                <div class="card">











         <div class="card-content">
          <div class="card-body table-responsive">
            <table class="table text-center m-0">
              <thead>
                <tr>
                  <th>No</th>
                  <th>Staff Name</th>
                  <th>Efficiency Delta</th>
                  <th>NPS Delta</th>
                  <th>Efficiency</th>
                  <th>Reported Issues</th>             
                </tr>
              </thead>            
              {data_staff && (
                    <tbody>
                    {data_staff.staff.map((dbObject) => (
                        <StaffSection key={dbObject.id} dbObject={dbObject} />
                    ))}
                    </tbody>
                )} 
            </table>
          </div>
        </div>




     </div>
    </div>
</div>
);
};
//gg

export default VisualsGraphs;
