import { useQuery, gql } from '@apollo/client';
import React from 'react';
import {
    LineChart,
    Line,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend,
    ReferenceLine,    
  } from 'recharts';

import PropTypes from 'prop-types';
import { BarChart, Bar, Cell } from 'recharts';
import { scaleOrdinal } from 'd3-scale';
import { schemeCategory10 } from 'd3-scale-chromatic';


const data_chart_foot_fall = [
    {
      name: 'Jan',
      amt: 2400,
    },
    {
      name: 'Feb',
      amt: 2210,
    },
    {
      name: 'March',
      amt: 2290,
    },
    {
      name: 'Apr',
      amt: 2000,
    },
    {
      name: 'May',  
      amt: 2181,
    },
    {
      name: 'Jun', 
      amt: 2500,
    },
    {
      name: 'Jul', 
      amt: 2100,
    },
  ];



const FOOTFALL_QUERY = gql`
{
    footfall {
      id
      name
      amnt
    }
  } 
`;

 const FootfallChart = () => {
 var { data } = useQuery(FOOTFALL_QUERY);


 const obj = [];

 for (var x in data.footfall) {
    //console.log(data.footfall[x].name)
    //.push(data.footfall[x].name,data.footfall[x].amnt)
    obj.push({"name": data.footfall[x].name , "amnt":data.footfall[x].amnt});
  }
 //var myJsonString = JSON.stringify(data.footfall);

console.log(obj);

 return (       
    
    <div class="card-body pt-0 chart_container">                
    <LineChart
            width={500}
            height={300}
            data={obj}
            margin={{
                top: 20,
                right: 50,
                left: 20,
                bottom: 5,
            }}
            >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Legend />
            <ReferenceLine x="Page C" stroke="red" label="Max PV PAGE" />
            <ReferenceLine y={9800} label="Max" stroke="red" />
            <Line type="monotone" dataKey="amt" stroke="#8884d8" />                   
    </LineChart>                                      
  </div>


);
};
export default FootfallChart;
